import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { BrowserRouter, Switch, Route } from 'react-router-dom'

// Routes
import Home from './pages/Home';
import Login from './pages/registrations/Login';
import Signup from './pages/registrations/Signup';

export default function App() {
  const [isLoggedIn, setLogged] = useState(false);
  const [user, setUser] = useState({})

  useEffect(() => {
    axios.get('http://localhost:3000/logged_in', { withCredentials: true })
      .then(response => {
        if (response.data.logged_in) {
          handleLogin(response)
        } else {
          handleLogout()
        }
      })
      .catch(error => console.log('Logged in Error:', error.message))
  }, [])

  function handleLogin(data) {
    setLogged(true);
    setUser(data.user);
    localStorage.setItem('userData', JSON.stringify(data.user))
  }

  function handleLogout() {
    setLogged(false);
    setUser({});
  }


  return (
    <div>
      <BrowserRouter>
        <Switch>
          <Route
            exact path='/'
            render={props => (
              <Home {...props} handleLogout={handleLogout} loggedInStatus={isLoggedIn} />
            )} />
          <Route
            exact path='/login'
            render={props => (
              <Login {...props} handleLogin={handleLogin} loggedInStatus={isLoggedIn} />
            )} />
          <Route
            exact path='/signup'
            render={props => (
              <Signup {...props} handleLogin={handleLogin} loggedInStatus={isLoggedIn} />
            )} />
        </Switch>
      </BrowserRouter>
    </div>
  );

}
